mapper.select:
SELECT
t.table_name AS tableName,
t.table_comment AS tableComment
FROM tables t


        AND t.table_schema = #{databaseName}


        AND t.table_name = #{tableName}

SELECT t.table_name AS tableName, t.column_name AS columnName, t.column_default AS columnDefault, t.is_nullable AS isNullable, t.data_type AS dataType, t.character_maximum AS characterMaximum, t.column_key AS columnKey, t.column_comment AS columnComment FROM tables t
AND t.table_schema = #{databaseName}
AND t.table_name = #{tableName}
